mapper.select:
    SELECT TEAM_NAME AS teamName
    FROM TEAM
    WHERE TEAM_NO =
    	CASE 
	        WHEN #{teamBNo} != 0 THEN #{teamBNo}
	        ELSE #{teamANo}
	    END;

mapper.update:
UPDATE MATCH_BEST
SET VOTE_STATUS = NOW()
WHERE MATCH_NO = #{matchNo} AND MEM_NO = #{memNo}

UPDATE MATCH_BEST
SET MEM_NO_VOTE_COUNT = MEM_NO_VOTE_COUNT + 1
WHERE MATCH_NO = #{matchNo} AND MEM_NO = #{bestMNo}

mapper.select:
SELECT AREA_NAME
FROM AREA
WHERE ACTIVITY_AREA = #{activityArea}

SELECT DISTINCT M.SCORE_A AS scoreA, M.SCORE_B AS scoreB, M.STADIUM_NO AS stadiumNo, M.MATCH_NO AS matchNo, A.TEAM_NAME AS teamAName, B.TEAM_NAME AS teamBName, M.TEAM_A_NO AS teamANo, M.TEAM_B_NO AS teamBNo, PA.CHANGE_NAME AS teamAProfile, PB.CHANGE_NAME AS teamBProfile FROM `MATCH` M JOIN TEAM A ON M.TEAM_A_NO = A.TEAM_NO JOIN TEAM B ON M.TEAM_B_NO = B.TEAM_NO JOIN TEAM_MEMBER TA ON M.TEAM_A_NO = TA.TEAM_NO JOIN TEAM_MEMBER TB ON M.TEAM_B_NO = TB.TEAM_NO LEFT JOIN PROFILE_FILE PA ON M.TEAM_A_NO = PA.TEAM_NO LEFT JOIN PROFILE_FILE PB ON M.TEAM_B_NO = PB.TEAM_NO JOIN STADIUM S ON M.STADIUM_NO = S.STADIUM_NO WHERE M.SCORE_A IS NOT NULL AND M.SCORE_B IS NOT NULL AND S.STADIUM_ADDRESS LIKE CONCAT('%', #{cityName}, '%', #{districtName}, '%')
AND M.CATEGORY = #{category}
ORDER BY M.MATCH_NO DESC LIMIT 6;
