mapper.update:
UPDATE MATCH_BEST
SET VOTE_STATUS = NOW()
WHERE MATCH_NO = #{matchNo} AND MEM_NO = #{memNo}

UPDATE MATCH_BEST
SET MEM_NO_VOTE_COUNT = MEM_NO_VOTE_COUNT + 1
WHERE MATCH_NO = #{matchNo} AND MEM_NO = #{bestMNo}

mapper.select:
SELECT AREA_NAME
FROM AREA
WHERE ACTIVITY_AREA = #{activityArea}
