mapper.insert:
insert into category(
MEM_NO, SOC_POSI, SOC_ABILITY, FOOT_POSI, FOOT_ABILITY, BASKET_POSI, BASKET_ABILITY, BASE_POSI, BASE_ABILITY
) values(
#{memNo}, #{socPosition}, #{socSkill}, #{futPosition}, #{futSkill}, #{basketPosition}, #{basketSkill}, #{basePosition}, #{baseSkill}
)

mapper.select:
SELECT STADIUM_NO as stadiumNo, STADIUM_ADDRESS as stadiumAddress, STADIUM_NAME as stadiumName, STADIUM_CATEGORY as stadiumCategory, STADIUM_START_TIME as stadiumStartTime, STADIUM_END_TIME as stadiumEndTime FROM STADIUM WHERE STATUS = 'Y'
AND ( STADIUM_NAME LIKE CONCAT('%', #{stadiumName}, '%') OR STADIUM_NAME LIKE '%축구장%' OR STADIUM_NAME LIKE '%풋살장%' OR STADIUM_NAME LIKE '%야구장%' OR STADIUM_NAME LIKE '%농구장%' )
AND STADIUM_ADDRESS LIKE CONCAT('%', #{stadiumAddress}, '%')
AND STADIUM_CATEGORY = #{stadiumCategory}
AND STADIUM_START_TIME >= #{stadiumStartTime}
AND STADIUM_END_TIME <= #{stadiumEndTime}
