mapper.select:
SELECT COUNT(*) 
FROM STADIUM S
JOIN AREA A
	ON S.STADIUM_ADDRESS LIKE CONCAT('%', REPLACE(A.AREA_NAME, '서울시', ''), '%')
WHERE S.STATUS = 'Y'

	AND A.ACTIVITY_AREA = #{activityArea} -- 지역 코드 필터링


    AND STADIUM_NAME LIKE CONCAT('%', #{stadiumName}, '%')


    AND STADIUM_CATEGORY = #{stadiumCategory}


    AND STADIUM_START_TIME >= #{stadiumStartTime}


    AND STADIUM_END_TIME <= #{stadiumEndTime}

SELECT S.STADIUM_NO as stadiumNo, S.STADIUM_ADDRESS as stadiumAddress, S.STADIUM_NAME as stadiumName, S.STADIUM_CATEGORY as stadiumCategory, S.STADIUM_START_TIME as stadiumStartTime, S.STADIUM_END_TIME as stadiumEndTime, A.ACTIVITY_AREA as activityArea, SI.FILE_PATH as filePath, SI.CHANGE_NAME as changeName FROM STADIUM S JOIN AREA A ON S.STADIUM_ADDRESS LIKE CONCAT('%', REPLACE(A.AREA_NAME, '서울시 ', ''), '%') LEFT JOIN STADIUM_IMG SI ON S.STADIUM_NO = SI.STADIUM_NO AND SI.FILE_TYPE = 0 AND SI.STATUS = 'Y' WHERE S.STATUS = 'Y'
AND A.ACTIVITY_AREA = #{activityArea}
AND S.STADIUM_NAME LIKE CONCAT('%', #{stadiumName}, '%')
AND S.STADIUM_CATEGORY = #{stadiumCategory}
AND S.STADIUM_START_TIME >= #{stadiumStartTime}
AND S.STADIUM_END_TIME <= #{stadiumEndTime}
LIMIT #{startRow}, #{boardLimit}

SELECT COUNT(*)
FROM STADIUM_REVIEW
WHERE STADIUM_NO = #{stadiumNo}

select
    t.TEAM_NO
FROM TEAM t
WHERE MEM_NO = #{memNo}
AND STATUS = 'Y'
and TEAM_CATEGORY =#{category};

select s.STADIUM_CATEGORY as category
from STADIUM s
where s.STADIUM_NO = #{stadiumNo};

